import React from "react";
import s from '../styles/dist/TodoItem.module.css'


const TodoItem = props => {
    let isCompleted = () => {
        if (props.completed === true) return s.completed;
    }

    let toggleCompleting = () => {
        props.toggleCompleting(props.id)
    }


    return (
        <div className={s.wrapper} >
            <div className={isCompleted()}>{props.text}</div>
            {
                props.completed ? <button className={s.lightgreen} toggleCompleting={toggleCompleting}>Выполнено!</button> : <button className={s.lightred} toggleCompleting={toggleCompleting}>Выполнить</button>
            }
        </div>
    )
};

export default TodoItem;
