import React from "react";
import s from '../styles/dist/Form.module.css'

const Form = props => {

    let input = React.createRef();
    

    return (
        <div className={s.wrapper}>
            <input type="text" placeholder='Что еще добавим..?' ref={input} value={props.inputValue} onChange={props.changeInputValue} />
            <button onClick={props.addTodo}>Добавить в список</button>
        </div>
    )
};

export default Form;
