import React from "react";
import s from '../styles/dist/TodoItemList.module.css'
import TodoItem from "./TodoItem";


const TodoItemList = props => {



    let list = props.todos.forEach(todo => {
        if (props.filter === 'all') {
            return (
                <TodoItem key={todo.id} id={todo.id} text={todo.text} completed={todo.completed} uncompleteTodo={props.uncompleteTodo} completeTodo={props.completeTodo} />
            )
        } else if (props.filter === 'completed' && todo.completed) {
            return (
                <TodoItem key={todo.id} id={todo.id} text={todo.text} completed={todo.completed} uncompleteTodo={props.uncompleteTodo} completeTodo={props.completeTodo} />
            )
        } else if (props.filter === 'uncompleted' && !todo.completed) {
            return (
                <TodoItem key={todo.id} id={todo.id} text={todo.text} completed={todo.completed} uncompleteTodo={props.uncompleteTodo} completeTodo={props.completeTodo} />
            )
        }
    })

    return (
        <div className={s.wrapper}>
            {list}
            <button className={s.uncomplete_button + ' button'} onClick={props.uncompleteAll}>Сделать все невыполненным!</button>
        </div>
    )
};

export default TodoItemList;
