import React from "react";
import s from '../styles/dist/Filter.module.css'

const Filter = props => {

    return (
        <div className={s.wrapper}>
            <label><input type="checkbox" name='completed' onChange={props.toggleCompletedFilter} checked={props.filters.completed ? true : false} />Выполненные</label>
            <label><input type="checkbox" onChange={props.toggleCompletedFilter} checked={props.filters.uncompleted ? true : false} />Невыполненные</label>
        </div>
    )
};

export default Filter;

