import React from "react";
import s from '../styles/dist/TodoItem.module.css'


const TodoItem = props => {
    let isCompleted = () => {
        if (props.completed === true) return s.completed;
    }

    let toggleCompleting = (id) => {
        props.toggleCompleting(id)
    }


    return (
        <div className={s.wrapper} onClick={() => (toggleCompleting(props.id))}>
            <div className={isCompleted() + ' ' + s}>{props.text}</div>
            <div className={s.right}>
                {props.completed ? <div className={s.lightgreen + ' ' + s.status} >выполнено</div> : <div className={s.lightred + ' ' + s.status} >не выполнено</div>}
                <div className={s.deleteButton}>&#10006;</div>
            </div>
        </div >
    )
};

export default TodoItem;
